$('.filter__item').click(function (event){
    $('.filter__item').removeClass('active');
    $(this).addClass('active');
    const i = $(this).data('filter');
    console.log(i);
    if(i===1){
        $('.gallery__column').show();
    }else{
        $('.gallery__column').hide();
        $('.gallery__column.f_' + i).show();
    }
    return false;
});


